import React from 'react';
import { Container, Section } from '../../globalStyles';
import {
	FeatureText,
	FeatureTitle,
	FeatureWrapper,
	FeatureColumn,
	FeatureImageWrapper,
	FeatureName,
	FeatureTextWrapper,FeatureTextBody,
} from './FeaturesStyles';
import { featuresData } from '../../data/FeaturesData';

const Features = () => {
	const initial = {
		y: 40,
		opacity: 0,
	};
	const animate = {
		y: 0,
		opacity: 1,
	};

	return (
		<>
			<Section smPadding="50px 10px" position="relative" inverse id="about">
			<Container>
				<FeatureTextWrapper>
					<FeatureTitle>About Us</FeatureTitle>
					<FeatureTextBody>
            <b>CA Titus Koshy</b>, a Chartered Accountant, proficient in
            Assurance, Advisory Engagements and Tax Consultancy, heads the Firm.
            Titus trained under the tutelage of CA Babu A Kallivayalil - Central
            Council Member at ICAI. At Babu A Kallivayalil & Co, he served as
            the Team Lead, managing Statutory Audits and Internal Audits.
          </FeatureTextBody>
          <FeatureTextBody>
            Titus is a <b>Certified Information Systems Auditor</b> (CISA). He
            has completed ICAI's Certificate Courses on <b>Concurrent Audit of Banks</b>, and 
            <b> Forensic Accounting & Fraud Detection</b>. ACCA is currently
            being pursued.
          </FeatureTextBody>
          <FeatureTextBody>
            Along with Titus, the core team is built around <b>Jithin John</b> and 
            <b> Gokul Sarma</b>, who are semi qualified in Chartered Accountancy, and
            <b> Alex K</b>, skilled in Information Systems.
          </FeatureTextBody>
		  <FeatureTextBody>The Team serves Clients primarily from <b>Banking, Entertainment & 
			Media, Insurance, Manufacturing, Retail</b> and <b>Service</b> Industries.  
          </FeatureTextBody>
				</FeatureTextWrapper>
				<FeatureWrapper>

				</FeatureWrapper>
			</Container>
		</Section>

			<Section smPadding="50px 10px" position="relative" inverse id="services">
			<Container>
				<FeatureTextWrapper>
					<FeatureTitle>What We Offer</FeatureTitle>
				</FeatureTextWrapper>
				<FeatureWrapper>
					{featuresData.map((el, index) => (
						<FeatureColumn
							initial={initial}
							animate={animate}
							transition={{ duration: 0.5 + index * 0.1 }}
							key={index}
						>
							<FeatureImageWrapper className={el.imgClass}>
								{el.icon}
							</FeatureImageWrapper>
							<FeatureName>{el.name}</FeatureName>
							<FeatureText>{el.description}</FeatureText>
						</FeatureColumn>
					))}
				</FeatureWrapper>
			</Container>
		</Section>
		</>
	);
};

export default Features;
